import { read as readNumber } from "visual/utils/reader/number";
import { read as readString } from "visual/utils/string/specs";
import "visual/utils/style2";
import { defaultValueValue } from "../onChange";
import { capByPrefix } from "../string";
import {
  styleMaskCustomShape,
  styleMaskPosition,
  styleMaskSize
} from "../style2/styleMask";
import { CSSValue } from "../style2/types";
import { cssStyleDropShadow } from "./cssStyleDropShadow";

export function cssStyleMaskShape({
  v,
  device,
  state,
  prefix = ""
}: CSSValue): string {
  const dvv = (key: string): unknown =>
    defaultValueValue({ v, key, device, state });

  const maskShape = readString(dvv(capByPrefix(prefix, "maskShape"))) ?? "none";

  if (maskShape === "none") {
    return "";
  }

  switch (maskShape) {
    case "none":
      return `-webkit-mask-image: none;
                      mask-image: none;`;
    case "circle":
      return `-webkit-mask-image: url(data:image/svg+xml;base64,);
                      mask-image: url(data:image/svg+xml;base64,);`;
    case "rhombus":
      return `-webkit-mask-image: url(data:image/svg+xml;base64,);
                      mask-image: url(data:image/svg+xml;base64,);`;
    case "star":
      return `-webkit-mask-image: url(data:image/svg+xml;base64,);
                      mask-image: url(data:image/svg+xml;base64,);`;
    case "flower":
      return `-webkit-mask-image: url(data:image/svg+xml;base64,);
                      mask-image: url(data:image/svg+xml;base64,);`;
    case "square":
      return `-webkit-mask-image: url(data:image/svg+xml;base64,);
                      mask-image: url(data:image/svg+xml;base64,);`;
    case "triangle":
      return `-webkit-mask-image: url(data:image/svg+xml;base64,);
                      mask-image: url(data:image/svg+xml;base64,);`;
    case "blob1":
      return `-webkit-mask-image: url(data:image/svg+xml;base64,);
                      mask-image: url(data:image/svg+xml;base64,);`;
    case "blob2":
      return `-webkit-mask-image: url(data:image/svg+xml;base64,);
                      mask-image: url(data:image/svg+xml;base64,);`;
    case "blob3":
      return `-webkit-mask-image: url(data:image/svg+xml;base64,);
                      mask-image: url(data:image/svg+xml;base64,);`;
    case "blob4":
      return `-webkit-mask-image: url(data:image/svg+xml;base64,);
                      mask-image: url(data:image/svg+xml;base64,);`;
    case "brush1":
      return `-webkit-mask-image: url(data:image/svg+xml;base64,);
                      mask-image: url(data:image/svg+xml;base64,);`;
    case "brush2":
      return `-webkit-mask-image: url(data:image/svg+xml;base64,);
                      mask-image: url(data:image/svg+xml;base64,);`;
    case "brush3":
      return `-webkit-mask-image: url(data:image/svg+xml;base64,);
                      mask-image: url(data:image/svg+xml;base64,);`;
    case "brush4":
      return `-webkit-mask-image: url(data:image/svg+xml;base64,);
                      mask-image: url(data:image/svg+xml;base64,);`;
    case "poly1":
      return `-webkit-mask-image: url(data:image/svg+xml;base64,);
                      mask-image: url(data:image/svg+xml;base64,);`;
    case "poly2":
      return `-webkit-mask-image: url(data:image/svg+xml;base64,);
                      mask-image: url(data:image/svg+xml;base64,);`;
    case "poly3":
      return `-webkit-mask-image: url(data:image/svg+xml;base64,);
                      mask-image: url(data:image/svg+xml;base64,);`;
    case "poly4":
      return `-webkit-mask-image: url(data:image/svg+xml;base64,);
                      mask-image: url(data:image/svg+xml;base64,);`;
    case "hexagon":
      return `-webkit-mask-image: url(data:image/svg+xml;base64,);
                      mask-image: url(data:image/svg+xml;base64,);`;
  }

  return "";
}

export function cssStyleMaskSize({
  v,
  device,
  state,
  prefix = ""
}: CSSValue): string {
  const dvv = (key: string): unknown =>
    defaultValueValue({ v, key, device, state });

  const maskShape = readString(dvv(capByPrefix(prefix, "maskShape"))) ?? "none";
  const maskSize = readString(dvv(capByPrefix(prefix, "maskSize"))) ?? "cover";
  const maskScale = readNumber(dvv(capByPrefix(prefix, "maskScale"))) ?? 50;
  const maskScaleSuffix =
    readString(dvv(capByPrefix(prefix, "maskScaleSuffix"))) ?? "px";

  if (maskShape === "none") {
    return "";
  }

  return styleMaskSize({ maskScaleSuffix, maskSize, maskScale });
}

export function cssStyleMaskRepeat({
  v,
  device,
  prefix = ""
}: CSSValue): string {
  const dvv = (key: string): unknown => defaultValueValue({ v, key, device });

  const maskRepeat =
    readString(dvv(capByPrefix(prefix, "maskRepeat"))) ?? "no-repeat";
  const maskShape = readString(dvv(capByPrefix(prefix, "maskShape")));
  const maskSize = readString(dvv(capByPrefix(prefix, "maskSize")));

  if (maskShape === "none" || maskSize === "cover") {
    return "";
  }

  return `-webkit-mask-repeat:${maskRepeat};mask-repeat:${maskRepeat};`;
}

export function cssStyleMaskPosition({
  v,
  device,
  state,
  prefix = ""
}: CSSValue): string {
  const dvv = (key: string): unknown =>
    defaultValueValue({ v, key, device, state });

  const maskShape = readString(dvv(capByPrefix(prefix, "maskShape"))) ?? "none";

  const maskPosition =
    readString(dvv(capByPrefix(prefix, "maskPosition"))) ?? "center center";
  const maskPositionx =
    readNumber(dvv(capByPrefix(prefix, "maskPositionx"))) ?? 50;
  const maskPositiony =
    readNumber(dvv(capByPrefix(prefix, "maskPositiony"))) ?? 50;
  const maskPositionxSuffix =
    readString(dvv(capByPrefix(prefix, "maskPositionxSuffix"))) ?? "%";
  const maskPositionySuffix =
    readString(dvv(capByPrefix(prefix, "maskPositionySuffix"))) ?? "%";

  if (maskShape === "none") {
    return "";
  }

  return styleMaskPosition({
    maskPosition,
    maskPositionx,
    maskPositiony,
    maskPositionxSuffix,
    maskPositionySuffix
  });
}

export function cssStyleMaskCustomShape({
  v,
  device,
  state,
  prefix = ""
}: CSSValue): string {
  const dvv = (key: string): unknown =>
    defaultValueValue({ v, key, device, state });
  const maskShape = readString(dvv(capByPrefix(prefix, "maskShape"))) ?? "none";

  if (maskShape === "none") {
    return "";
  }

  return styleMaskCustomShape({ v, device, state, prefix });
}

export function cssStyleMaskDropShadow({
  v,
  device,
  state,
  prefix = "maskShadow"
}: CSSValue): string {
  const dvv = (key: string): unknown =>
    defaultValueValue({ v, key, device, state });

  const maskShape = readString(dvv("maskShape"));

  return maskShape === "none"
    ? ""
    : cssStyleDropShadow({ v, device, state, prefix });
}
